mapper.select:
SELECT userid, password, nickname
FROM user
WHERE userid = #{userid}

SELECT distance, cal, time, lastride
FROM record
WHERE userid = #{userid}

mapper.insert:
INSERT INTO record (distance, cal, time, lastride)
VALUES (#{distance},#{cal} #{time}, NOW())
WHERE userid = #{userid}

mapper.select:
SELECT count(*)
FROM record
WHERE distance   <  #{userDist}

SELECT count(*)
FROM record
WHERE distance = #{userDist}
